import axios from "axios";
import {BASE_URL} from "../constants/urls"
import{goToRecipesList} from "../routers/coordinator"

export const login=(body,clear,history,setRightButtonText,setIsLoading)=>{
   setIsLoading(true)  
    axios.post(`${BASE_URL}/user/login`,body
    ).then((res)=>{
       localStorage.setItem('token', res.data.token)
       clear()
       setIsLoading(false)  
       goToRecipesList(history)
       setRightButtonText('Logout') 
    }).catch((err)=>{
       alert (err.response.data.message)
       setIsLoading(false)  
    })
};

export const SingUp=(body,clear,history,setRightButtonText,setIsLoading)=>{
   setIsLoading(true)  
   axios.post(`${BASE_URL}/user/signup`,body).then((res)=>{
      localStorage.setItem('token', res.data.token)
      clear()
      setIsLoading()
      goToRecipesList(history)
      setRightButtonText('Logout')
   }).catch((err)=>{
      alert (err.response.data.message)
   })
};
