import React from "react";
import { InputsContainer } from './styled'
import { Button, TextField } from "@material-ui/core";
import useForm from '../../hooks/useForm';
import axios from "axios";
import {BASE_URL} from "../../constants/urls"

export default function LoginForm() {
    const [form, onChange, clear] = useForm({ email:"", password:""})

    const onSubmitForm = (e) => {
        e.preventDefault()
        login(form, clear, history, setRightButtonText, setIsLoading)
    };

    const login=()=>{

        axios.post(`${BASE_URL}/user/login`,form).
        then((res)=>{
           localStorage.setItem('token', res.data.token)
        }).
        catch((err)=>{
           alert ('Nome ou email não cadastrado')
        })
    };


    return (

        <InputsContainer>
            <form onSubmit={onSubmitForm}>
                <TextField
                    name={"email"}
                    value={form.email}
                    onChange={onChange}
                    label={"E-mail"}
                    variant={'outlined'}
                    fullWidth
                    margin={"normal"}
                    required
                    type={"email"}
                />
                <TextField
                    name={"password"}
                    value={form.password}
                    onChange={onChange}
                    label={"Senha"}
                    variant={'outlined'}
                    fullWidth
                    margin={"normal"}
                    required
                    type={"password"}
                />
                <Button
                    type={'submit'}
                    fullWidth
                    variant={'contained'}
                    color={'primary'}
                >Fazer Login</Button>
            </form>

        </InputsContainer>


    );

};
