import React, { useState } from 'react';
import { InputsContainer } from '../LoginPage/styled';
import { Button, TextField,CircularProgress  } from '@material-ui/core';
import useForm from '../../hooks/useForm';
import { SingUp } from "../../services/user";
import { useHistory } from 'react-router';

export default function SingUpForm({ setRightButtonText}) {
    const [form, onChange, clear] = useForm({ email: "", password: "", name: "" })
    const [isLoading, setIsLoading]=useState(false)
    const history = useHistory ()

    const onSubmit = (e) => {
        e.preventDefault()
        SingUp(form, clear, history, setRightButtonText)
    };

    return (
        <InputsContainer>
            <form onSubmit={onSubmit}>

                <TextField
                    name={"name"}
                    value={form.name}
                    onChange={onChange}
                    label={"Nome"}
                    variant={'outlined'}
                    fullWidth
                    margin={"normal"}
                    required
                    type={"password"}
                />
                <TextField
                    name={"email"}
                    value={form.email}
                    onChange={onChange}
                    label={"E-mail"}
                    variant={'outlined'}
                    fullWidth
                    margin={"normal"}
                    required
                    type={"email"}
                />
                <TextField
                    name={"password"}
                    value={form.password}
                    onChange={onChange}
                    label={"Senha"}
                    variant={'outlined'}
                    fullWidth
                    margin={"normal"}
                    required
                    type={"password"}
                />
                <Button
                    type={'submit'}
                    fullWidth
                    variant={'contained'}
                    color={'primary'}
                >{isLoading? <CircularProgress color={'inherit'} size={24}/> : <> Fazer Cadastro</>}</Button>
            </form>

        </InputsContainer>

    );





};
