import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import theme from '../../constants/theme';
import { StyledToolbar } from './styled';
import { goToRecipesList, goToLogin } from '../../routers/coordinator';
import { useHistory } from 'react-router';
import { Logout } from '@mui/icons-material';

export default function Header() {
    const token= localStorage.getItem('token')

    const history=useHistory()

    const [rightButtonText, setRightButtonText]=React.useState(token ? 'Logout':'Login')

    const logout=()=>{
      localStorage.removeItem('token')
    };

    const rightButtonAction=()=>{
      if (token){
        logout()
        setRightButtonText('Login')
        go
      }
    };

    return (

      <AppBar position="static" theme={theme}>
        <StyledToolbar>
          <Button  onClick={()=>goToRecipesList (history)} color="inherit"> Cookenu</Button>
          <Button  onClick={()=> goToLogin (history)} color="inherit">{rightButtonText}</Button>
        </StyledToolbar>
      </AppBar>

  );
}
